const bel = require('bel')
const csjs = require('csjs-inject')

module.exports = footer

function footer ({ tools = [] } = {}) {
  const element = bel`<div class="tools">
    <h3> tools </h3>
    <ul class="chapter-list">${
      tools.map(({ title, url }) => bel`<li class="chapter"><a href="${url}">${title}</a></li>`)
    }</ul>
  </div>`
  return element


}
